import Link from "next/link";
import { useRouter } from "next/router";
import { useEffect, useState } from "react";
import styled from "styled-components";
import { BPSmall, BPMobileM } from "./BreakPoint";
import dynamic from "next/dynamic";

const OverlayContainer = dynamic(() => import("./Overlay"), {
  ssr: false,
});

const HeaderWrapper = styled.div`
  height: 65px;
`;
const HeaderContainer = styled.header`
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  top: 0;
  left: 0;
  position: relative;
  z-index: 40;
  width: 100%;
  user-select: none;
  transition: all 0.4s ease-in-out;
  background-color: #fffefc;
  position: fixed;

  &.fixed {
    background-color: #fff;
    box-shadow: 0 1px 4px hsl(0deg 4% 15% / 10%);
  }
`;
const HeaderRow = styled.div`
  width: 100%;
  padding: 10px 10px;
  position: relative;

  z-index: 5;

  @media (min-width: 1200px) {
    padding: 10px 20px;
  }
`;
const HeaderRowContainer = styled.div`
  display: flex;
  align-items: center;
  flex-flow: row wrap;
  justify-content: space-between;
  width: 100%;
  padding: 0px 15px;
  margin: 0 auto;

  @media (min-width: ${BPSmall}) {
    padding: 0px 30px;
  }
`;
const HeaderLogoWrapper = styled.div`
  display: flex;
  align-items: center;
`;
const ToggleHolder = styled.div`
  display: inline-flex;
  cursor: pointer;
  align-items: center;
  @media (min-width: 600px) {
    display: none;
  }
`;
const ToggleHolderLogo = styled.div`
  display: none;
  @media (min-width: 600px) and (max-width: 1200px) {
    cursor: pointer;
    align-items: center;
    margin-right: 10px;
    display: inline-flex;
  }
`;
const MobileToggle = styled.div`
  display: inline-flex;
  width: 18px;
  height: 16px;
  align-items: center;
  transition: none;
  vertical-align: middle;
  position: relative;

  & span:nth-child(1) {
    top: -1px;
  }
  & span:nth-child(2) {
    top: 6px;
  }
  & span:nth-child(3) {
    top: 13px;
  }

  @media (max-width: ${BPMobileM}) {
    width: 15px;
    & span:nth-child(1) {
      top: 1px;
    }
    & span:nth-child(2) {
      top: 6px;
    }
    & span:nth-child(3) {
      top: 11px;
    }
  }
`;
const ToggleBar = styled.span`
  position: absolute;
  width: 18px;
  height: 2px;
  background: #1e88e5;
  content: " ";
  left: 0;

  @media (max-width: ${BPMobileM}) {
    width: 15px;
  }
`;
const LogoContainer = styled.div`
  display: inline-flex;
  flex-shrink: 0;
  margin: 0 auto 0 5px;
  max-width: 100%;
`;
const LogoLink = styled.a`
  display: inline-flex;
  align-items: center;
  position: relative;
  color: #42a5f5;
  width: 200px;
  @media (max-width: ${BPMobileM}) {
    width: 180px;
  }
`;

const HeaderNavigationWrapper = styled.div`
  @media (max-width: 1200px) {
    display: none;
  }
`;
const HeaderNavigationContainer = styled.div`
  display: inline-flex;
  margin: 0 auto;
`;
const HeaderNavigationMenu = styled.ul`
  display: inline-flex;
  margin: 0;
  padding: 0;
  list-style: none;
  line-height: 1;
  flex-wrap: wrap;
`;
const HeaderNavigationItem = styled.li`
  display: inline-flex;
  white-space: nowrap;
  margin-left: 50px;
  position: relative;

  &::before {
    content: "";
    background: #196fe0;
    height: 3px;
    width: 100%;
    position: absolute;
    left: 0;
    right: 0;
    bottom: -1px;
    opacity: 0;
    visibility: hidden;
    transform: scale3d(0, 1, 0.5);
    transition: all 0.3s ease;
    transform-origin: left;
  }

  &:hover::before,
  &.active::before {
    transform: scale3d(1, 1, 1);
    opacity: 1;
    visibility: visible;
  }
`;
const HeaderNavigationItemLink = styled.a`
  min-height: 45px;
  display: flex;
  align-items: center;
  font-weight: 400;
`;
const HeaderActionWrapper = styled.div`
  display: flex;
  align-items: center;
  padding: 0;
`;
const HeaderActionContainer = styled.div`
  margin-left: auto;
  display: flex;
`;
const HeaderActionMenu = styled.ul`
  display: flex;
  align-items: center;
  margin: 0 0 0 auto;
  list-style: none;
  justify-content: space-between;
  flex-wrap: wrap;
  width: 100%;

  @media (max-width: 600px) {
    width: 0;
  }
`;
const HeaderActionItem = styled.li`
  display: inline-flex;
  margin-left: 20px;
  text-align: left;
`;
const HeaderSignInButton = styled.a`
  display: inline-flex;
  align-items: center;
  font-size: 16px;
  font-weight: 700;
  border: none;
  background: none;
  cursor: pointer;
  outline: none;
  text-decoration: none;
  color: #2196f3;
  transition: none;
  &:hover {
    color: #1e88e5;
  }

  @media (max-width: 700px) {
    display: none;
  }
`;
const HeaderSignUpButton = styled.a`
  display: inline-flex;
  align-items: center;
  font-size: 16px;
  font-weight: 700;
  padding: 10px 25px;
  background-color: #2196f3;
  border-radius: 25px;
  border: none;
  cursor: pointer;
  outline: none;
  text-decoration: none;
  color: #fff;
  transition: none;
  &:hover {
    background-color: #1e88e5;
  }
  @media (max-width: 600px) {
    display: none;
  }
`;

